(5, 1, 0, 'contacto', '', 0, 3, '', 0, '<!DOCTYPE html>\n\n<html xmlns="http://www.w3.org/1999/xhtml" >\n<head>\n    <title>ANPASA</title>\n    <link rev="made" href="mailto:webmaster@anpasa.com" />\n    <meta name="keywords" content="" />\n    <meta name="description" content="" />\n    <meta name="author" content="" />\n    <meta name="ROBOTS" content="" />\n	<meta http-equiv="X-UA-Compatible" content="IE=EmulateIE7" > \n\n<meta http-equiv="Content-Type" content="text/html; charset=[[++modx_charset]]" />\n<base href="[[++site_url]]/" />\n<title>[[++site_name]]</title>\n\n<link href="assets/templates/anpasa/css/estilos.css" rel=''stylesheet'' type=''text/css''>\n<link href="assets/templates/anpasa/css/fuentes.css" rel=''stylesheet'' type=''text/css''>\n<link href="assets/templates/anpasa/css/cabecera.css" rel=''stylesheet'' type=''text/css''>\n<link href="assets/templates/anpasa/css/menu.css" rel=''stylesheet'' type=''text/css''>\n<link href="assets/templates/anpasa/css/contenidos.css" rel=''stylesheet'' type=''text/css''>\n<link href="assets/templates/anpasa/css/footer.css" rel=''stylesheet'' type=''text/css''>\n<link href="assets/templates/anpasa/css/contacto.css" rel=''stylesheet'' type=''text/css''>\n<link href="assets/templates/anpasa/css/ajaxsearch.css" rel=''stylesheet'' type=''text/css''>\n\n\n\n\n\n<!-- inicio script para contacto -->\n<script type="text/javascript" src="http://ajax.googleapis.com/ajax/libs/jquery/1.7/jquery.min.js"></script>\n\n<script type="text/javascript">\njQuery(document).ready(function ($) { \n	$(''#send'').click(function(){ \n		$(''.error'').fadeOut(''slow''); \n\n		var error = false; \n\n		var name = $(''input#name'').val(); \n		if(name == "" || name == " ") {\n			$(''#err-name'').fadeIn(''slow''); \n			error = true; \n		}\n\n		var email_compare = /^([a-z0-9_.-]+)@([da-z.-]+).([a-z.]{2,6})$/; \n		var email = $(''input#email'').val(); \n		if (email == "" || email == " ") { \n			$(''#err-email'').fadeIn(''slow''); \n			error = true;\n		}else if (!email_compare.test(email)) { \n			$(''#err-emailvld'').fadeIn(''slow''); \n			error = true;\n		}\n\n		if(error == true) {\n			$(''#err-form'').slideDown(''slow'');\n			return false;\n		}\n\n		var data_string = $(''#ajax-form'').serialize(); \n		alert(data_string);\n\n		$.ajax({\n			type: "POST",\n			url: $(''#ajax-form'').attr(''action''),\n			data: data_string,\n			timeout: 6000,\n			error: function(request,error) {\n				if (error == "timeout") {\n					$(''#err-timedout'').slideDown(''slow'');\n				}\n				else {\n					$(''#err-state'').slideDown(''slow'');\n					$("#err-state").html(''An error occurred: '' + error + '''');\n				}\n			},\n			success: function() {\n				$(''#ajax-form'').slideUp(''slow'');\n				$(''#ajaxsuccess'').slideDown(''slow'');\n			}\n		});\n\n		return false; \n	}); \n});\n\n</script>\n\n\n\n		<link rel="stylesheet" href="css/colorbox.css" />\n		<script src="https://ajax.googleapis.com/ajax/libs/jquery/1.9.1/jquery.min.js"></script>\n		<script src="js/jquery.colorbox.js"></script>\n		<script>\n			$(document).ready(function(){\n				//Examples of how to assign the Colorbox event to elements\n				$(".group1").colorbox({rel:''group1''});\n				$(".group2").colorbox({rel:''group2'', transition:"fade"});\n				$(".group3").colorbox({rel:''group3'', transition:"none", width:"75%", height:"75%"});\n				$(".group4").colorbox({rel:''group4'', slideshow:true});\n				$(".ajax").colorbox();\n				$(".youtube").colorbox({iframe:true, innerWidth:425, innerHeight:344});\n				$(".vimeo").colorbox({iframe:true, innerWidth:500, innerHeight:409});\n				$(".iframe").colorbox({iframe:true, innerWidth:1000, innerHeight:450});\n				$(".inline").colorbox({inline:true, width:"50%"});\n				$(".callbacks").colorbox({\n					onOpen:function(){ alert(''onOpen: colorbox is about to open''); },\n					onLoad:function(){ alert(''onLoad: colorbox has started to load the targeted content''); },\n					onComplete:function(){ alert(''onComplete: colorbox has displayed the loaded content''); },\n					onCleanup:function(){ alert(''onCleanup: colorbox has begun the close process''); },\n					onClosed:function(){ alert(''onClosed: colorbox has completely closed''); }\n				});\n\n				$(''.non-retina'').colorbox({rel:''group5'', transition:''none''})\n				$(''.retina'').colorbox({rel:''group5'', transition:''none'', retinaImage:true, retinaUrl:true});\n				\n				//Example of preserving a JavaScript event for inline calls.\n				$("#click").click(function(){ \n					$(''#click'').css({"background-color":"#f00", "color":"#fff", "cursor":"inherit"}).text("Open this window again and this message will still be here.");\n					return false;\n				});\n			});\n		</script>\n\n\n</head>\n\n<body>\n<div id="contenedor">\n<header>\n[[$cabecera]]\n</header>\n<div id="submenu">\n<div class="block" >\n<div class="menu-centrado">\n[[!Wayfinder?  \n&startId=`[[*alias]]` \n&level=`1` \n&outerTpl=`outerTpl` \n&rowTpl=`submenu` \n&lastClass=`submenu` \n&firstClass=`submenu`\n&ignoreHidden=`1`\n\n]]\n</div>\n</div>\n\n</div>\n[[*content]]\n\n		\n</div>\n<div class=footer-final>		\n<footer>\n[[$footer-gris]]\n</footer>\n\n\n\n\n</div>\n\n\n\n\n\n\n\n\n\n\n\n\n</body>\n</html>', 0, 'a:0:{}', 0, ''),
